package com.company.day007;

public class A012 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char ch = 'A';
		System.out.println('C'-ch); //2
		System.out.println('5'-'4'); //1
		
		System.out.println(ch+1); //66
		System.out.println(++ch); //67
		System.out.println(ch++); //67
				
		
		
	}

}
