package com.company.day014;

public class Arr1Ex009 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] su = { 10, 20, 30, 40, 50 };
		int box = 0;

		for (int i = 0; i < su.length; i++) {
			box = box + su[i];

		}

		System.out.println("sum = " + box);
		

	}

}
