package com.company.day018;

public class Method003 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		myDouble(1,2);
		myDouble(1.1, 1.2);
		myDouble('a', 'b');
		
		
		
		
	}//void END

	public static void myInt( int a) {
		System.out.println(a);
	}
	public static void myFloat( float a) {
		System.out.println(a);
	}
	public static void myDouble( double a, double b) {
		System.out.println(a+b);
	}
}//class END
