package com.company.day020;

public class Method015 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("[BASIC001] 1. 매개변수의 자료형");
		print(4);
		print(1.2);
		print('A');
		print("Happy");
		
		
		System.out.println("[BASIC001] 2. 매개변수의 갯수");
		
		
	}
	
	static void print(int i) {
		System.out.println(i);
	}
	static void print(double i) {
		System.out.println(i);
	}

	static void print(char i) {
		System.out.println(i);
	}
	static void print(String i) {
		System.out.println(i);
	}

}
