package pjw;

public class Q003 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//출력값 예측
		int a = 10;
		int b = 10;
		
		System.out.println(++a); 		//11
		System.out.println(b++);		//10
		System.out.println(a++ + --b);	//21
		System.out.println(a+b);		//22
		
		
	}
}
