package pjw;

public class Q005 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//출력값 예측
		System.out.println(10<<2);//40
		System.out.println(10>>2);//2
		System.out.println(-10<<2);//-40
		System.out.println(-10>>2);//-3
		System.out.println(-10>>>30);//3
		
	}

}
